Return<int32_t> Aproximity::poll(int32_t precision) {
    this->pollCallCount++;
    this->lastPollCalledMs = now(); 
    if (this->fd < 0) {
        return -1;
    }
    int shimPrecision = static_cast<int>(precision);
    int32_t result =
        static_cast<int32_t>(poll_sensor(this->fd, shimPrecision));
    return result;
}
